INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_CIV5_SPAIN_TERCIO_HELP', 'Unidad militar dominante en la época del Renacimiento, exclusiva de España. El Tercio tiene una mayor fuerza de combate [ICON_STRENGTH] que el Mosquete al que sustituye, y una bonificación contra unidades montadas.', '', '');
