INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_GEM_VERSION', 'GEM v1.{1_num}', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-13'), 'TXT_KEY_MISSION_START_GOLDENAGE_HELP', 'Esta orden consumirá al Gran Personaje y comenzará una Edad de Oro [ICON_GOLDEN_AGE] (+20% de producción en todas las ciudades) durante {1_Num} turnos. Cada vez que un Gran Personaje comienza una Edad de Oro, la duración se reducirá (hasta un mínimo de 3 turnos).', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-13'), 'TXT_KEY_TRADE_ROUTE_INCOME_INFO', '+{1_Num} Oro [ICON_GOLD] por ruta comercial[NEWLINE]+{2_Num} Oro[ICON_GOLD] por ciudadano [ICON_CITIZEN] en la capital[NEWLINE]+{3_Num} Oro [ICON_GOLD] por Ciudadano [ICON_CITIZEN]', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-13'), 'TXT_KEY_MISSION_HURRY_PRODUCTION_HELP', 'Otorga (200 + 30 * población [ICON_CITIZEN]) Producción [ICON_PRODUCTION] para la construcción actual de la ciudad. Esto consume al Gran Personaje.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-13'), 'TXT_KEY_PRODUCTION_BUILDING_ALLOWS_WATER_ROUTES_EXTRA', 'Una ruta comercial [ICON_CONNECTED] de carreteras, ferrocaril o marítima a la Capital produce Oro [ICON_GOLD].[NEWLINE][NEWLINE]Una ruta de ferrocarril [ICON_CONNECTED_RAILROAD] o marítima a la Capital proporciona +25% Producción [ICON_PRODUCTION] (con la tecnología [COLOR_POSITIVE_TEXT]Ferrocarril[ENDCOLOR]).[NEWLINE][NEWLINE]Los [COLOR_POSITIVE_TEXT]Faros[ENDCOLOR] proporcionan una conexión segura entre rutas comerciales marítimas y terrestres.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_IMPROVEMENT_TRADING_POST', 'Villa', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_CIV5_IMPROVEMENTS_TRADING_POST_TEXT', 'Una villa es un grupo de asentamientos o comunidad con una población que oscila entre cientos y varios miles habitantes. Estos pequeños asentamientos se unen a tu sociedad económica.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_CIV5_IMPROVEMENTS_TRADINGPOST', 'Villa', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_GOLD_TRADINGPOST_ADV_QUEST', '¿Proporciona oro la villa?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_GOLD_TRADINGPOST_HEADING3_BODY', 'Construir una mejora de villa en una casilla para mejorar su rendimiento de oro.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_GOLD_TRADINGPOST_HEADING3_TITLE', 'La Villa', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_WORKERS_TRADINGPOST_HEADING3_BODY', 'La Villa incrementa la producción de oro en 1 en una casilla. No necesita acceso a un recurso.[NEWLINE]Tecnología Necesaria: Caza[NEWLINE]Tiempo de Construcción: 5 turnos[NEWLINE]Puede ser Construida en: Cualquier tipo de tierra, excepto hielo', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_BUILD_TRADING_POST', 'Construir una [LINK=IMPROVEMENT_TRADING_POST]Villa[\LINK]', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_BUILD_GOLD_REC', 'Incrementará la cantidad de Oro [ICON_GOLD] producida por esta casilla, ¡aumentando la cantidad de dinero que tendremos para gastar!', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_TP_GOLDEN_AGE_EFFECT', 'Las Ciudades ganan +20% de producción durante Edades de Oro [ICON_GOLDEN_AGE].[NEWLINE]+20% Excedente de alimentos [ICON_FOOD][NEWLINE]+20% Producción [ICON_PRODUCTION][NEWLINE]+20% Oro [ICON_GOLD][NEWLINE]+20% Ciencia [ICON_RESEARCH][NEWLINE]+20% Cultura [ICON_CULTURE][NEWLINE]+20% Fe [ICON_PEACE][NEWLINE][NEWLINE]El excedente de felicidad sigue añadiendose al contador de Edad de Oro mientras estás en una Edad de Oro.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_BUILDING_MACHU_PICHU_HELP', 'Una montaña cerca de la ciudad gana:[NEWLINE]Alimento: 5 [ICON_FOOD][NEWLINE]Oro: 5 [ICON_GOLD][NEWLINE]Cultura: 5 [ICON_CULTURE][NEWLINE]Fe: 5 [ICON_PEACE]', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-15'), 'TXT_KEY_BUILDING_FLOATING_GARDENS_DESC', 'Chinampa', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-15'), 'TXT_KEY_BUILDING_FOATING_GARDENS_STRATEGY', 'El Chinampa es el edificio único Azteca, remplaza al Molino de Agua. Incrementa el flujo de alimentos a la ciudad, y solo puede ser construido si la ciudad está cerca de un lago o un río.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-15'), 'TXT_KEY_BUILDING_FLOATING_GARDENS_PEDIA', 'Un Chinampa  es una pequeña isla artificial construida en un lago de agua dulce para aumentar la cantidad de tierras disponibles para la agricultura. El lago irriga la isla y la provee con material organico. El resultado es un entorno increiblemente fértil. Los Aztecas eran maestros en este tipo de agriculura, y los ''Jardines Flotantes de Xochimilco'' siguen siendo famosos hoy en día.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-15'), 'TXT_KEY_BUILDING_SANITATION_SYSTEM', 'Sistema de Saneamiento', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-15'), 'TXT_KEY_BUILDING_SANITATION_SYSTEM_PEDIA', 'El saneamiento es el medio por el que a través de la higiene se mejora la salud y la prevención del contacto humano con los residuos tóxicos.[NEWLINE][NEWLINE]Los residuos pueden ser agentes infecciosos físicos, microbiológicos, biologicos o químicos. LA higiene que se encarga de la prevención puede proporcionarse a través de soluciones de ingeniería (como el alcantarillado o el tratamiento de aguas residuales), tecnologías simples (letrinas, tanques sépticos), o incluso con prácticas personales de higiene (lavándose las manos con jabón).', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-15'), 'TXT_KEY_BUILDING_VACCINATIONS', 'Vacunas', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-15'), 'TXT_KEY_BUILDING_VACCINATIONS_PEDIA', 'Una vacuna es el preparado biológico que mejora la inmunidad a un tipo particular de enfermedad. La vacuna estimula el sistema inmunitario del cuerpo para reconocer un agente de enfermedad, destruirlo y "recordarlo", por lo que el sistema inmune puede reconocer y destruir más facilmente cualquiera de esos microorganismos en posteriores encuentros. La Vacunación es el método más efectivo en prevención de enfermerdades infecciosas.[NEWLINE][NEWLINE]El esfuerzo global de la humanidad en la vacunación erradicó la viruela, y restringió otras enfermedades mortales como la polio, sarampión, y tétano. La Viruela mató aproximadamente un 90% delos Nativos Americanos después del contacto con Eurasia en los 1500s. Cuando finalmente fue erradicada en 1979, la viruale había matado aproximadamente 300–500 millones de personas en el mundo tan solo en el pasado siglo, más que todas las guerras del siglo 20 juntas. La Viruela fue la primera enfermedad para la que se produjo una vacuna en 1796. La inmunización fue llamada vacuna porque ésta derivaba de un virus que afectaba a las vacas (Latin: vacca - vaca).', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-15'), 'TXT_KEY_BUILDING_WORKSHOP_STRATEGY', 'El Taller incrementa la producción cuando construyes edificios. Las Ciudades en colinas no pueden construir talleres.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-15'), 'TXT_KEY_BUILDING_STABLE_STRATEGY', 'El Establo incrementa la velocidad de producción de las unidades montadas y añade producción por ciertos recursos.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-15'), 'TXT_KEY_BUILDING_MINT_STRATEGY', 'La Ceca incrementa los ingresos de Oro [ICON_GOLD] de la Ciudad y los ingresos de oro por ciertos productos.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-15'), 'TXT_KEY_BUILDING_WAREHOUSE', 'Almacén', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-15'), 'TXT_KEY_BUILDING_WAREHOUSE_PEDIA', 'Un Almacén es un edificio comercial para el almacenamiento de mercancías. Los Almacenes son usados por los fabricantes, importadores, exportadores, distribuidores, transportistas, clientes, etc. Las mercancías almacenadas abarcan cualquier tipo de materia prima, materiales empaquetados, repuestos, componentses, o bienes manufacturados asociados con la agricultura, fabricación, o comercio.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-16'), 'TXT_KEY_BUILDING_MENTORS_HALL', 'Sala de Mentores', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-16'), 'TXT_KEY_BUILDING_MENTORS_HALL_PEDIA', 'La Mentoría es un proceso organizado para la transmisión de conocimientos, capital social y el apoyo psicosocial del mentor al discípulo para objetivos laborales o desarrollo profesional. La Mentoría se basa en una comunicación informal, normalmente cara a cara, y durante un periodo de tiempo sostenido entre una persona reconocida por tenern un gran conocimiento, sabiduría o experiencia (el mentor) y una persona que desea aprender de él (el discípulo).', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-16'), 'TXT_KEY_BUILDING_RADIO_STATION', 'Estación de Radio', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-16'), 'TXT_KEY_BUILDING_RADIO_STATION_PEDIA', 'La radiodifusión es una transmisión sin cables de una sola dirección a través de ondas de radio con la intención de alcanzar una amplia audiencia.[NEWLINE][NEWLINE]Las primeras estaciones de radio eran simples sistemas de telégrafo y no transmitían audio. En los inicios de 1920s, las primeras estaciones de audio fueron establecidas por equipos radiofónicos de fabricantes y minoristas que ofrecían programas de radio con la intención de vender radios a los oyentes.[NEWLINE][NEWLINE]Cuando la práctica de la publicidad se popularizó, cada programa de radio era normalmente patrocinado por negocios particulares a cambio de una breve mención del nombre de su empresa al inicio y al final del programa patrocinado. Sin embargo, los dueños de las estaciones de radio pronto se dieron cuenta de que podían ganar más dinero vendiendo los derechos de patrocinio en pequeños márgenes de tiempo durante las emisiones de su estación de radio, mucho más que vendiendo los derechos de patrocinio a un único negocio por programa.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-16'), 'TXT_KEY_BUILDING_STONE_WORKS_STRATEGY', 'Las Canteras solo pueden construirse en una ciudad cerca de un un recurso mejorado de Piedra [ICON_RES_STONE] o Mármol [ICON_RES_MARBLE]. Las Canteras incrementan la producción [ICON_PRODUCTION] de la ciudad.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-16'), 'TXT_KEY_BUILDING_ARENA', 'Arena', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-16'), 'TXT_KEY_BUILDING_ARENA_PEDIA', 'Una Arena es un area cerrada, normalmente circular o con forma de óvalo, frecuentemente diseñado para eventos y espectáculos deportivos. La palabra deriva del Latín harena, se utilizaba una arena particularmente fina para absorber la sangre en las antiguas arenas como los coliseos en Roma.[NEWLINE][NEWLINE]Las Arenas están compuestas por un gran espacio abierto rodeado por todos o la mayoría de sus lados por varios niveles de asientos para los espectadores. La clave principal de una arena es que el espacio para el evento se encuentra en el punto más bajo, permitiendo una maxima visibilidad. Una Arena está normalmente diseñada para acomodar un enorme número de espectadores.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-16'), 'TXT_KEY_BUILDING_PUBLISHING_HOUSE', 'Editorial', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-16'), 'TXT_KEY_BUILDING_PUBLISHING_HOUSE_PEDIA', 'La Publicación es la actividad de hacer pública la información al público general. En algunos casos, los autores pueden ser sus propios editores, lo que significa que los autores del contenido también proveen a los medios entregando y mostrando el contenido por igual.[NEWLINE][NEWLINE]Tradicionalmente, el término se refiere a la distribución de trabajos escritos como los libros y revistas. Con la aparición de los sistemas de información digital e Internet, el alcance de la publicación se expandió para incluir los recursos electrónicos, como las versiones electrónicas de libros y publicaciones periódicas, además de micgropublicaciones, websites blogs, publicaciones de videojuegos y similares.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-16'), 'TXT_KEY_BUILDING_CINEMA', 'Cine', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-16'), 'TXT_KEY_BUILDING_CINEMA_PEDIA', 'Una Sala de Cine es un lugar para ver películas.[NEWLINE][NEWLINE]La mayoría aunque no todos los cines son operaciones comerciales dirigidas al público general, que asiste mediante la compra de una entrada. La película se proyecta con un proyector de películas en una gran pantalla enfrente de la sala. La mayoría de las salas de cine estan equipadas hoy en día con proyectores digitales, eliminando la necesidad del transporte físico de la cinta.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_BUILDING_SUPERMAX_PRISON', 'Prisión de Máxima Seguridad', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_BUILDING_SUPERMAX_PRISON_PEDIA', 'La Prisión de Máxima Seguridad es el nombre usado para describir prisiones de "unidad de control", o unidades sin prisiones, lo que representa los máximos niveles de seguridad en custodia en sistemas penales de algunos países. El obejtivo es proporcionar a largo plazo la segregación residencial de los reclusos clasificados como de alto riesgo en el sistema penitenciario - los criminales "lo peor de lo peor" y aquellos que suponen una amenaza para la seguridad nacional e internacional.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-16'), 'TXT_KEY_BUILDING_CAPITAL_BUILDING', 'Capital', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-16'), 'TXT_KEY_BUILDING_CAPITAL_BUILDING_PEDIA', 'La sede del gobierno es el edificio, complejo de edificios o ciudad desde donde el gobierno ejerce su autoridad. La sede del gobierno se encuentra normalmente en la capital. Sin embargo, en algunos países la sede del gobierno difiere de la capital. por ejemplo en Holada donde la Haya es la sede del gobierno y Amsterdan es la capital jurídica de Holanda. En la mayoría, suele ser la misma ciudad, por ejemplo, Moscú es la capital y sede de gobierno de Rusia. En el Reino Unido, la sede de gobierno es Londres, la capital, o más específicamente la Ciudad de Westminster.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-16'), 'TXT_KEY_BUILDING_GREAT_HALL', 'Ayuntamiento', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-18'), 'TXT_KEY_BUILDING_GREAT_HALL_PEDIA', 'El Ayuntamiento es el edificio administrativo del jefe de una ciudad, pueblo u otro municipio. Normalmente alberga el consejo, sus departamentos asociados y sus empleados de la ciudad o pueblo. También por lo general funciona como la base del alcalde de una ciudad, pueblo, ciudad, provincia o región.[NEWLINE][NEWLINE]El gobierno local puede tratar de utilizar el edificio de ayuntamiento para promover y mejorar la calidad de vida de la comunidad. En muchos casos, los "''ayuntamientos'' sirven, no solo de edificios para el gobierno, sino también como instalaciones para varias actividades cívicas y culturaless. Estos pueden incluir exposiciones de arte, representaciones teatrales, exposiciones y festivales. Como símbolos de gobierno local, los ayuntamientos de la ciudad suelen tener una arquitectura distintiva, y los edificios pueden tener un gran significado histórico.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-16'), 'TXT_KEY_BUILDING_PALACE', 'Palacio', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_BUILDING_PALACE_PEDIA', 'Un palacio es una gran residencia, habitualmente una residencia real o la casa de un jefe de Estado o de algún otro dignatario de alto rango, como un obispo o arzobispo.[NEWLINE][NEWLINE]La palabra deriva del nombre latino Palatium, del Monte Palatino, una de las siete colinas de Roma. En muchas partes de Europa, el término también se aplica a los ambiciosos mansiones privadas de la aristocracia. Muchos palacios históricos están ahora sometidos a otros usos, como parlamentos, museos, hoteles o edificios de oficinas. La palabra también se usa a veces para describir un edificio ricamente ornamentado utilizado para espectáculos públicos o exhibiciones.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-16'), 'TXT_KEY_POPUP_CITY_CAPTURE_INFO', '¿Qué quieres hacer con esta Ciudad?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_POPUP_ANNEX_PUPPET', '[ICON_OCCUPIED] [COLOR_POSITIVE_TEXT]Ocupar[ENDCOLOR]: Mejor para las grandes ciudades. La construcción de una Delegación la convierte en una ciudad normal .[NEWLINE][NEWLINE][COLOR_POSITIVE_TEXT]Hacer Títere[ENDCOLOR][ICON_PUPPET]: Mejor para ciudades pequeñas. Los títeres tienen 3 de Infelicidad [ICON_HAPPINESS_4] básicos por ciudad (en vez de 4) y no incrementa los costes de Políticas.[NEWLINE][NEWLINE]Las ciudades Ocupadas y Títeres producen menos rendimiento que una ciudad normal.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_POPUP_ANNEX_CITY', 'Ocupar la Ciudad', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_POPUP_CITY_CAPTURE_INFO_PUPPET', 'Puedes Ocupar [ICON_OCCUPIED] una ciudad Títere en cualquier momento.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-04-02'), 'TXT_KEY_CIV5_BABYLON_WALLS_STRATEGY', 'Las Murallas de Babilonia son el Edificio exclusivo de Babilonia, remplaza las murallas normales. Las Murallas de Babilonia incrementan la fuerza defensiva de la ciudad y los puntos de golpe de la ciudad (ambas considerablemente superiores a las de las murallas normales) .', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-18'), 'TXT_KEY_BUILDING_WALLS_STRATEGY', 'Las Murallas incrementan la fuerza defensiva de la ciudad y puntos de golpe, haciéndola más difícil de conquistar. Las Murallas son muy útiles para ciudades situadas en las fronteras de la civilización.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-18'), 'TXT_KEY_BUILDING_MILITARY_BASE_STRATEGY', 'La Base Militar es un edificio de mitad del juego que aumenta la fuerza defensiva de la ciudad y sus puntos de golpe. La ciudad debe poseer un Arsenal antes de que la Base Militar se pueda construir', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-18'), 'TXT_KEY_BUILDING_CASTLE_STRATEGY', 'El castillo es un edificio de la época medieval que aumenta la fuerza defensiva de la ciudad y sus puntos de golpe. La ciudad debe poseer una Muralla antes de que el Castillo se pueda construir.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-18'), 'TXT_KEY_BUILDING_ARSENAL_STRATEGY', 'El Arsenal es un edificio militar de mitad del juego que aumenta la fuerza defensiva y puntos de vida de una ciudad, haciendo que la ciudad sea más difícil de capturar. La ciudad debe poseer un Castillo antes de que se pueda construir un Arsenal.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-18'), 'TXT_KEY_BUILDING_MUGHAL_FORT_STRATEGY', 'El Fuerte mogol es un edificio de la época medieval en que aumenta la fuerza defensiva de la ciudad, puntos de golpe, y proporciona Cultura [ICON_CULTURE] cada turno. Es el edificio único de India, y remplaza el Castillo. Tras descubrir de descubrir el Vuelo, proporcionará también un bonus de Oro [ICON_GOLD] a la Ciudad. Las Murallas son un prerequisito para construir el Fuerte Mogol.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-18'), 'TXT_KEY_BUILDING_GARDEN_STRATEGY', 'El Jardín aumenta la velocidad a la que se generan Grandes Personajes [ICON_GREAT_PEOPLE] en la ciudad.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_BUILDING_HEROIC_EPIC_HELP', 'Todas las nuevas unidades entrenadas en esta Ciudad reciben la promoción [COLOR_POSITIVE_TEXT]Moral[ENDCOLOR], la cual mejora la fuerza de combate [ICON_STRENGTH] un 15%.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_BUILDING_OXFORD_UNIVERSITY', 'Universidad de Heidelberg', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_BUILDING_HOLY_PALACE', 'Palacio Sagrado', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_BUILDING_HOLY_PALACE_HELP', 'Proporciona +8 [ICON_FAITH] Fé y 25% de Fé en la ciudad que se construya.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-20'), 'TXT_KEY_BUILDING_HOLY_PALACE_STRATEGY', 'El Palacio Sagrado provee un aumento significativo en la [ICON_FAITH] fé de la ciudad.', '', '');
