INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_PROMOTION_CONSCRIPTION', 'Conscripción.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_PROMOTION_CONSCRIPTION_HELP', '+10 a la [COLOR_POSITIVE_TEXT] tasa de sanación [ENDCOLOR].', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_PROMOTION_PROFESSIONAL_ARMY', 'Ejército Profesional.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_PROMOTION_PROFESSIONAL_ARMY_HELP', 'Desbloquea promociones avanzadas.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_PROMOTION_MERCANTILISM', 'Mercantilismo.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_PROMOTION_MERCANTILISM_HELP', '+2 al movimiento [ICON_MOVES].', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_PROMOTION_FREEDOM', 'Libertad.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_PROMOTION_FREEDOM_HELP', '+25% a la fuerza [ICON_STRENGTH] en territorio amigo.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICIES_CULTURAL_VICTORY', 'Al terminar seis ramas políticas, puedes construir el [COLOR_POSITIVE_TEXT] Proyecto Utopía [ENDCOLOR] en una de tus ciudades, provocando una [COLOR_POSITIVE_TEXT] Victoria cultural [ENDCOLOR]!', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_BRANCH_LIBERTY_HELP', 'Bueno para ganar rápidamente territorio al inicio de la partida. [NEWLINE] [NEWLINE] [COLOR_POSITIVE_TEXT] Al abrirla [ENDCOLOR] [NEWLINE] +1 Palacio en las 3 ciudades más antiguas. [NEWLINE] +1 de Cultura [ICON_CULTURE] en todas las ciudades. [NEWLINE] [NEWLINE] [COLOR_POSITIVE_TEXT] Al concluirla [ENDCOLOR] [COLOR_GREY] (terminar todas las políticas en este árbol) [ENDCOLOR] [NEWLINE] +3 a la producción [ICON_PRODUCTION] en las ciudades con un especialista. [NEWLINE] +1 Gran personaje [ICON_TEAM_2] de su elección.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_COLLECTIVE_RULE_HELP', '+1 colono [ICON_TEAM_2] en la Capital. [NEWLINE] +20% a la producción [ICON_PRODUCTION] de colonos.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_MERITOCRACY_HELP', 'Da una suma fija de oro [ICON_GOLD]. [NEWLINE] [NEWLINE] Reduce los costos políticos de los grandes imperios, por lo que las ciudades con teatros están en renta neutra por tasa política.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_CITIZENSHIP_HELP', '+2 trabajadores [ICON_TEAM_2] en la Capital. [NEWLINE] +25% a la  velocidad al realizar mejoras con trabajadores [ICON_WORKER].', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_REPRESENTATION_HELP', '+1 de  felicidad [ICON_HAPPINESS_1] nacional en las ciudades conectadas [ICON_CONNECTED]. [NEWLINE] -5% de infelicidad [ICON_HAPPINESS_4] por Población [ICON_CITIZEN] en ciudades que no están ocupadas [ICON_OCCUPIED].', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_REPUBLIC_HELP', '+2 a la producción de edificios defensivos [ICON_STRENGTH]. [NEWLINE] Construyes 1 Edificio defensivo [ICON_STRENGTH] en las ciudades actuales y futuras.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_BRANCH_TRADITION_HELP', 'Bueno para el desarrollo de un número reducido de grandes ciudades. [NEWLINE] [NEWLINE] [COLOR_POSITIVE_TEXT] Al inciarse [ENDCOLOR] [NEWLINE] +1 de Cultura [ICON_CULTURE] por cada superávit de felicidad [ICON_HAPPINESS_1] nacional . [NEWLINE] [NEWLINE] [COLOR_POSITIVE_TEXT] Al concluirse [ENDCOLOR] [COLOR_GREY] (terminar todas las políticas en este árbol) [ENDCOLOR] [NEWLINE] +50% a la tasa de nacimiento de Grandes Personajes [ICON_GREAT_PEOPLE]. [NEWLINE] +1 de felicidad [ICON_HAPPINESS_1] en la ciudad  por Maravillas. [NEWLINE] 200% a la tasa de expansión de la frontera cultural [ICON_CULTURE] en casillas lejos de las ciudades.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_ORAL_HISTORY', 'Narradores.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_ORAL_HISTORY_HELP', '+2 de Cultura [ICON_CULTURE] por el Palacio y Pueblos.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_ORAL_HISTORY_TEXT', 'La tradición oral es material cultural y tradición transmitida oralmente de una generación a otra. Los mensajes o testimonios se transmiten verbalmente en el habla o en canciones y puede tomar cualquier forma, por ejemplo, de cuentos populares, refranes, romances, canciones o cantos. De esta manera, es posible para una sociedad transmitir historia verbal, literatura verbal, leyes verbal y otros conocimientos a través de generaciones sin un sistema de escritura.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_CEREMONIAL_RITES', 'Ritos ceremoniales.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_CEREMONIAL_RITES_HELP', '+2 de cultura [ICON_CULTURE] por Edificios Culturales. [NEWLINE] Construye 1 Edificio Cultural [ICON_CULTURE] en sus 5 ciudades más antiguas.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_CEREMONIAL_RITES_TEXT', 'Una ceremonia es un acontecimiento de significación ritual realizado en una ocasión especial. Las ceremonias pueden tener un despliegue físico o componente teatral: la danza, una procesión, la imposición de manos. Un pronunciamiento declarativo verbal puede estar asociada con la ocasión. [NEWLINE] [NEWLINE] Una ceremonia puede marcar un rito de iniciación en la vida humana, marcando la importancia del nacimiento, la edad adulta, la iniciación en un grupo, o la muerte. A veces, una ceremonia sólo puede ser realizada por una persona con cierta autoridad. En sociedades más extensas, las ceremonias pueden marcar los eventos anuales o estacionales o recurrentes, como el equinoccio de primavera, solsticio de invierno y otras posiciones astronómicas anuales.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_LANDED_ELITE_HELP', '+20% por excedentes de alimentos [ICON_FOOD] en cada ciudad. [NEWLINE] +2 de alimentos [ICON_FOOD] por  edificios defensivos [ICON_STRENGTH].', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_OLIGARCHY_HELP', '+50% a la Fuerza [ICON_RANGE_STRENGTH] para las ciudades en guarnición. [NEWLINE] Las unidades de guarnición no tienen coste de mantenimiento [ICON_GOLD] y aumento de 0,2 de Experiencia [ICON_WAR] por turno.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_ARISTOCRACY_HELP', '+20% a la producción [ICON_PRODUCTION] en construcción de Maravillas. [NEWLINE] +2 de Cultura [ICON_CULTURE] por Maravillas.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_MONARCHY_HELP', '+5 de Ciencia [ICON_RESEARCH] por turno. [NEWLINE] +5% de ciencia [ICON_RESEARCH] por edificios de ciencia.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_BRANCH_HONOR_HELP', 'Bueno para luchar contra los bárbaros, y conquistar enemigos. [NEWLINE] [NEWLINE] [COLOR_POSITIVE_TEXT] Al iniciarse [ENDCOLOR] [NEWLINE] revela campamentos bárbaros. [NEWLINE] +10% a la fuerza contra bárbaros. [NEWLINE] Matar bárbaros da 5 de Cultura [ICON_CULTURE] por 1 de fuerza [ICON_STRENGTH]. [NEWLINE] [NEWLINE] [COLOR_POSITIVE_TEXT] AL concluirse [ENDCOLOR] [COLOR_GREY] (terminar todas las políticas en este árbol) [ENDCOLOR] [NEWLINE] +1 de felicidad [ICON_HAPPINESS_1] en la ciudad y +2 de cultura [ICON_CULTURE] para las ciudades con una guarnición o cuarteles.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_MILITARY_TRADITION_HELP', '+2 a las Unidades de Defensa [ICON_TEAM_2] en la Capital. [NEWLINE] +20% a la producción [ICON_PRODUCTION] por unidades y edificios militares.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_SPOILS_OF_WAR', 'Botín de guerra.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_SPOILS_OF_WAR_HELP', 'Usted rescata oro [ICON_GOLD] por equipo de los enemigos derrotados. [NEWLINE] +25% por premios instantáneos al capturar ciudades-estado [ICON_CITY_STATE].', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_SPOILS_OF_WAR_TEXT', 'Los saqueos por un ejército victorioso durante la guerra ha sido una práctica común en toda la historia. Para los soldados de a pie, que fue visto como una manera de complementar sus ingresos a menudo escasos y fue parte de la celebración de la victoria. En niveles más altos, la exposición orgullosa de un botín era una parte integral del triunfo romano típico, y Genghis Khan no era inusual en proclamar que la mayor felicidad era "vencer a tus enemigos... para robarles sus riquezas..." [NEWLINE] [NEWLINE] En la guerra en la antigüedad, el botín de guerra incluía las poblaciones derrotadas, que eran esclavizadas a menudo, y las mujeres y niños, que fueron integrados con frecuencia en la población del país victorioso. En otras sociedades pre modernas, los objetos fabricados con metales preciosos fueron el blanco preferido de los saqueos de guerra, en gran parte debido a su fácil traslado. En muchos casos, el saqueo fue una oportunidad para obtener tesoros que de otra manera no se hubieran podido obtener. Desde el siglo 18, las obras de arte se han convertido en un objetivo cada vez más popular. En la década de 1930 y más aún durante la Segunda Guerra Mundial, la Alemania nazi dedicaba en gran escala al saqueo organizado de arte y bienes.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_WAR_EPICS', 'Guerra Épicas.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_WAR_EPICS_HELP', '+1 de felicidad [ICON_HAPPINESS_1] en la ciudad por edificios culturales.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_WAR_EPICS_TEXT', 'Una épica (del adjetivo griego antiguo ἐπικός (epikos), de ἔπος (EPOS) "palabra, historia, poema" ") es un poema narrativo largo, normalmente sobre un tema serio que contiene detalles de los hechos heroicos y hechos significativos para una cultura o nación. [NEWLINE] [NEWLINE] Las epopeyas de guerra son poemas épicos o unas obras literarias de alcance épico que buscan o se crean para captar y expresar la esencia o el espíritu de una nación en particular, o un grupo étnico o lingüístico con aspiraciones de independencia o autonomía. Las Epopeyas nacionales con frecuencia relatan el origen de una nación, una parte de su historia, o un evento crucial como victorias decisivas o la derrota de los enemigos acérrimos, en el desarrollo de la identidad nacional.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_MILITARY_CASTE_HELP', '+1 de felicidad [ICON_HAPPINESS_1] en la ciudad y +2 de cultura [ICON_CULTURE] para cada ciudad con una guarnición.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_DISCIPLINE_HELP', '+1 Gran General  [ICON_TEAM_7] en la Capital. [NEWLINE] +1 al movimiento [ICON_MOVES] de Grandes Generales. [NEWLINE] 15% a la fuerza de combate [ICON_STRENGTH] por unidades adyacentes a unidades militares amistosos.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_PROMOTION_DISCIPLINE_HELP', '+15% de [ICON_STRENGTH] por unidades [COLOR_POSITIVE_TEXT] adyacentes [ENDCOLOR] a unidades militares amistosas.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_PROFESSIONAL_ARMY_HELP', '-25% por el coste de mantenimiento [ICON_GOLD] de unidades. [NEWLINE] -50% por el coste [ICON_GOLD] de actualizaciones de unidades.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_WARRIOR_CODE_HELP', '[COLOR_POSITIVE_TEXT] Código del guerrero [ENDCOLOR][NEWLINE]', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_BRANCH_PIETY_HELP', 'Bueno para la felicidad y la religión. [NEWLINE] [NEWLINE] [COLOR_POSITIVE_TEXT] Al iniciarse [ENDCOLOR] [NEWLINE] +5 de felicidad [ICON_HAPPINESS_1] nacional. [NEWLINE] +50 de fe [ICON_PEACE]. [NEWLINE] [NEWLINE] [COLOR_POSITIVE_TEXT] Al concluirse [ENDCOLOR] [COLOR_GREY] (terminar todas las políticas en este árbol) [ENDCOLOR] [NEWLINE] -25% el coste de compras con fe [ICON_PEACE].', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_CHARITY', 'Caridad.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_CHARITY_HELP', '+5% de oro [ICON_GOLD] por edificios religiosos.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_CHARITY_PEDIA', 'La palabra "caridad" viene de la antigua palabra francesa "Charité", que, a su vez, se deriva del latín "caritas". [NEWLINE] [NEWLINE] La práctica de la caridad significa la donación voluntaria de ayuda a los necesitados que no están relacionados con el donante, aunque el significado original está más involucrado con los conceptos de valor inapreciable, afecto, valor alto. Las donaciones de caridad se realizan directamente o por medio de un fondo de caridad u otras causas nobles. Estas instituciones han evolucionado para llevar a cabo el trabajo de ayudar a los pobres que llegaron a ser llamadas "obras de caridad". En la actualidad ''la beneficencia'' tiempos proporciona la mayor parte de donaciones caritativas, en términos de valor monetario. Los tipos de organizaciones benéficas incluyen orfanatos, bancos de alimentos, institutos religiosos dedicados al cuidado de los pobres, hospitales, organizaciones que visitan los confinados y encarcelados, y muchos otros.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_INSPIRATION', 'Inspiración.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_INSPIRATION_HELP', '+1 política social [ICON_TEAM_7] gratis. [NEWLINE] +2 de cultura [ICON_CULTURE] por edificios religiosos.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_INSPIRATION_PEDIA', 'La palabra "inspiración" viene del sustantivo latino Inspiratio y de la inspirare verbo. Inspirare es un término compuesto resultante del prefijo latino (en el interior, hacia) y el verbo spirare (para respirar). [NEWLINE] [NEWLINE] En época clásica romana, inspirare ya había llegado a significar "respirar profundamente" y asumió también el sentido figurado de "infundir [algo] en el corazón o en la mente de alguien". [NEWLINE] [NEWLINE] Por lo tanto ''inspiración'' se refiere a una explosión de creatividad inconsciente en una obra literaria, musical o de otro esfuerzo artístico. El concepto tiene sus orígenes en el helenismo y en el hebraísmo. Los griegos creían que la inspiración vino de las musas, así como de los dioses Apolo y Dionisio. Del mismo modo, en las antiguas religiones nórdicas, la inspiración proviene de los dioses, como Odin. La inspiración es también un asunto divino en la poesía hebrea. En el libro del profeta Amosthe habla de ser abrumado por la voz de Dios y obligado a hablar. En el cristianismo, la inspiración es un don del Espíritu Santo.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_TOLERANCE', 'Tolerancia.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_TOLERANCE_HELP', '+1 de felicidad [ICON_HAPPINESS_1] en la ciudad por Santuarios y templos.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_TOLERANCE_PEDIA', 'La tolerancia es "la práctica de permitir deliberadamente o permitir una cosa de la que uno desaprueba. Uno significativamente puede hablar de tolerancia, es decir, de permitir o autorizar, sólo si uno está en una posición de no permitir". También ha sido definido como "de soportar o aguantar" o "para nutrir, sustentar o preservar”. La tolerancia puede significar "no más que paciencia y el permiso de existir dado por los seguidores de una religión dominante a otra religión, a pesar de que estos últimos se miraban con desaprobación o como inferiores, erróneos o perjudiciales" [NEWLINE] [NEWLINE] la tolerancia es una actitud mental que implica la aceptación sin prejuicios de diferentes estilos de vida y creencias, mientras que la tolerancia implica poner a algo que uno desaprueba. [NEWLINE] [NEWLINE] ejemplos históricos de "tolerancia" abundan. Por ejemplo, en un acto de tolerancia, el rey persa Ciro el Grande creía que debía liberar de su cautiverio a los Judíos en 539-530 A.C, y permitió el regreso a su tierra natal de estos.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_UNITY', 'Unión.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_UNITY_HELP', 'Edad de Oro [ICON_GOLDEN_AGE] por 10 Turnos. [NEWLINE] Se requiere 25% menos de felicidad [ICON_HAPPINESS_1] nacional para Edades de Oro [ICON_GOLDEN_AGE].', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_UNITY_PEDIA', 'Unidad se refiere a los lazos en una sociedad que unen a las personas entre sí. El término se emplea generalmente en la sociología y otras ciencias sociales. [NEWLINE] [NEWLINE] En las sociedades simples pueden basarse principalmente en torno a parentescos y valores compartidos. En las sociedades más complejas, hay varias teorías en cuanto a lo que contribuye a un sentido de solidaridad social [NEWLINE] [NEWLINE] Esto introduce el término asociado de la "solidaridad" de la que se dice: [NEWLINE] "La solidaridad no supone que nuestras luchas son las mismas luchas, o que nuestro dolor es el dolor mismo, o que nuestra esperanza es el mismo futuro. Solidaridad implica el compromiso y el trabajo, así como el reconocimiento de que, incluso si no tenemos los mismos sentimientos, o la misma vida o los organismos mismos, vivimos en un terreno común. "- Sara Ahmed', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_DEVOTION', 'Devoción.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_DEVOTION_HELP', '+3 de fe [ICON_PEACE] por turno. [NEWLINE] +1 de fe [ICON_PEACE] por ciudad.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_DEVOTION_PEDIA', 'La devoción se puede definir como: el afecto ardiente, a menudo desinteresado y la dedicación, a una persona o principio. Culto, del inglés antiguo worthscipe, que significa mérito o adoración, es un acto de devoción religiosa por lo general dirigida a una deidad. En estrecha relación con el concepto del amor, la devoción es a menudo la esperanza de ser correspondido con algún tipo de don o bendición. [NEWLINE] [NEWLINE] Las personas que practican la devoción y que son llamadas a recibir estas bendiciones, son a menudo de mente cerrada hasta el punto de considerar el fanatismo. Así, mientras que el concepto de ''devoción'' es benigno, muchos ejemplos históricos de la devoción religiosa son a menudo extremos y sangrientos, como devotos compiten por el afecto de los devotos con demostraciones indignantes.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_BRANCH_COMMERCE', 'Riqueza.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_BRANCH_COMMERCE_CAP', 'RIQUEZA.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_CIV5_POLICY_COMMERCE_HEADING', '{TXT_KEY_POLICY_BRANCH_COMMERCE}', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_SOCIALPOLICY_COMMERCE_HEADING3_TITLE', '{TXT_KEY_POLICY_BRANCH_COMMERCE}', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_COMMERCE_TEXT', 'La riqueza es la abundancia de recursos valiosos o posesiones materiales. El concepto de riqueza es significativa en todos los ámbitos de la economía, sin embargo, el significado de la riqueza depende del contexto y no hay ninguna definición universalmente aceptada. En el nivel más general de la riqueza es "cualquier cosa de valor", que captura tanto el carácter subjetivo de la palabra, y la idea de que no es un concepto fijo o estático.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_SOCIALPOLICY_COMMERCE_ADV_QUEST', '¿De qué trata la rama sobre la política social Riqueza?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_SOCIALPOLICY_COMMERCE_HEADING3_BODY', 'La rama de la riqueza se concentra en el desarrollo amplio del territorio, con una oferta nacional de oro [ICON_GOLD] creado por las ciudades principales para comprar nuevas unidades y edificios en ciudades fronterizas subdesarrolladas.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_TECH_COMMERCE_HEADING4_BODY', 'La rama de la riqueza se concentra en el desarrollo amplio del territorio, con una oferta nacional de oro [ICON_GOLD] creado por las ciudades principales para comprar nuevas unidades y edificios en ciudades fronterizas subdesarrolladas. Ver las normas sobre Políticas Sociales para obtener más información.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_TECH_COMMERCE_HEADING4_TITLE', 'La Rama de La Riqueza.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_BRANCH_COMMERCE_HELP', 'Bueno para construir cosas más rápido en las ciudades. [NEWLINE] [NEWLINE] [COLOR_POSITIVE_TEXT] Al iniciarse [ENDCOLOR] [NEWLINE] +15% de ingreso nacional bruto [ICON_GOLD]. [NEWLINE] Puede comprar Grandes Mercaderes con fe [ICON_PEACE] una vez que entras en la era industrial. [NEWLINE] [NEWLINE] [COLOR_POSITIVE_TEXT] Al concluirse [ENDCOLOR] [COLOR_GREY] (terminar todas las políticas en este árbol) [ENDCOLOR] [NEWLINE] +1 felicidad [ICON_HAPPINESS_1] nacional por cada recurso de lujo, incluidos los que tengan superávit.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_MERCANTILISM_HELP', '+2 al movimiento [ICON_MOVES] de unidades fuera de combate. [NEWLINE] +25% de Ingresos [ICON_GOLD] por Comercio Interior [ICON_CONNECTED]. [NEWLINE] -25% por El costo [ICON_GOLD]  de carreteras y ferrocarriles.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_GUILDS', 'Gremios.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_GUILDS_HELP', '+1 Gran mercader [ICON_TEAM_7] en la Capital. [NEWLINE] +100% de oro [ICON_GOLD] por  misiones con Grandes Mercaderes.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_GUILDS_TEXT', 'Un gremio es una asociación de artesanos en un comercio particular. Los primeros tipos de gremio se formaron como cofradías de trabajadores. Estaban organizados en una combinación entre un sindicato, un cartel y una sociedad secreta. A menudo dependía de las concesiones de patentes de invención por un monarca o de otra autoridad para hacer cumplir el flujo de comercio a sus afiliados autónomos, y conservar la propiedad de las herramientas y el suministro de materiales.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_PROTECTIONISM_HELP', '-20% el coste [ICON_GOLD] por comprar unidades y edificios.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_PATENT_LAW', 'Derecho de Patentes.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_PATENT_LAW_HELP', '+1 de oro [ICON_GOLD] por Pueblos. [NEWLINE] +2 de oro [ICON_GOLD] por Puertos.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_PATENT_LAW_TEXT', 'Una patente es un conjunto de derechos exclusivos concedidos por un Estado (gobierno nacional) a un inventor o su cesionario por un período limitado de tiempo a cambio de la divulgación de una invención. [NEWLINE] [NEWLINE] Las patentes en el sentido moderno se originaron en 1474, cuando la República de Venecia promulgó un decreto que los dispositivos nuevos e innovadores, una vez puestos en práctica, debían ser comunicados a la República para obtener el derecho de impedir a otros su utilización. [NEWLINE] [NEWLINE] Inglaterra siguió con la ley de Monopolios en 1623 bajo el reinado de Jaime I, que declaró que las patentes sólo podían concederse por "proyectos de nueva invención." Durante el reinado de la reina Ana (1702-14), los abogados de La Corte Inglesa desarrollaron el requisito que la invención debía ser detallada y presentada por escrito. El sistema de patentes en muchos otros países se basa en la ley británica y se remonta a la Ley de Monopolios.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_MERCHANT_NAVY_HELP', '+1 al movimiento [ICON_MOVES] y +1 a la visión para buques de guerra. [NEWLINE] +2 de ciencia [ICON_RESEARCH] por ciudades costeras. [NEWLINE] +2 de ciencia [ICON_RESEARCH] por Edificios Navales.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_BRANCH_PATRONAGE_HELP', 'Bueno para interactuar con las ciudades-estado. [NEWLINE] [NEWLINE] [COLOR_POSITIVE_TEXT] Al iniciarse [ENDCOLOR] [NEWLINE] La influencia [ICON_INFLUENCE] de las ciudades-estado reposa en 20. [NEWLINE] [NEWLINE] [COLOR_POSITIVE_TEXT] Al concluirse [ENDCOLOR] [COLOR_GREY ] (terminar todas las políticas en este árbol) [ENDCOLOR] [NEWLINE] +2 de  felicidad [ICON_HAPPINESS_1] nacional por cada ciudad-estado amiga, y +3 por cada aliado.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_PHILANTHROPY_HELP', '+33% de influencia [ICON_INFLUENCE] por regalos de oro [ICON_GOLD] a las ciudades-estado.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_CULTURAL_DIPLOMACY_HELP', 'La influencia [ICON_INFLUENCE] con las ciudades-estado se degrada un 33% más lento de lo normal.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_AESTHETICS_HELP', '+20 de influencia [ICON_INFLUENCE] inmediata con las ciudades-estado. [NEWLINE] La influencia [ICON_INFLUENCE] de otros jugadores con las ciudades-estado se reduce un 25% más rápido.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_SCHOLASTICISM_HELP', '+2 de ciencia [ICON_RESEARCH] por época por cada ciudad-estado amiga, y +3 por cada aliado (dividida entre sus ciudades).', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_TRADE_PACT', 'Tratado Comercial.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_TRADE_PACT_HELP', '+2 de producción [ICON_PRODUCTION] por época por cada ciudad-estado amiga, y +3 por cada aliado (dividida entre sus ciudades).', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_TRADE_PACT_TEXT', 'El libre comercio es un sistema de política comercial que permite comerciar a través de fronteras nacionales, sin la interferencia de los gobiernos respectivos. Debido al principio económico de la ventaja comparativa, proporciona a los socios comerciales beneficios mutuos. En virtud de esta política, los precios son el único determinante de la asignación de recursos, y un fiel reflejo de la oferta y la demanda.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_BRANCH_ORDER_HELP', 'Bueno para la expansión y el desarrollo de las ciudades modernas. [NEWLINE] [NEWLINE] [COLOR_POSITIVE_TEXT] Al iniciarse [ENDCOLOR] [NEWLINE] +10% a la producción [ICON_PRODUCTION] en todas las ciudades. [NEWLINE] +1 Gran ingeniero [ICON_TEAM_7] en la Capital. [NEWLINE] Puede comprar Grandes Ingenieros con fe [ICON_PEACE]. [NEWLINE] [NEWLINE] [COLOR_POSITIVE_TEXT] Al concluirse [ENDCOLOR] [COLOR_GREY] (terminar todas las políticas en este árbol) [ENDCOLOR] [NEWLINE] da 10 turnos de producción [ICON_PRODUCTION] en todas las ciudades. [NEWLINE] +10% a la producción [ICON_PRODUCTION] de fábricas y plantas de energía.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_PLANNED_ECONOMY_HELP', 'Construye edificios de producción al doble de velocidad.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_SOCIALISM_HELP', '-25% por el coste [ICON_GOLD] de mantenimiento de edificios.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_LABOR_UNIONS', 'Sindicatos.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_LABOR_UNIONS_HELP', '+1 a la producción [ICON_PRODUCTION] por minas, canteras aserraderos y barcos pesqueros.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_LABOR_UNIONS_TEXT', 'Un sindicato o asociación de trabajadores, es una organización de trabajadores que se han unido para alcanzar objetivos comunes, tales como la protección de la integridad de su comercio, el logro de mejores salarios, el aumento del número de empleados que un empleador contrata, y mejores condiciones de trabajo. [NEWLINE ] [NEWLINE] Con origen en Europa, los sindicatos se hicieron populares en muchos países durante la Revolución Industrial, cuando la falta de habilidades necesarias para llevar a cabo la mayoría de los puestos de trabajo desplaza el poder de negociación  de un empleo casi en su totalidad a la parte patronal, causando a muchos trabajadores a ser maltratados y mal pagados. El sindicato, a través de su liderazgo, negocia con el empleador en nombre de los miembros del sindicato (los socios de base) y negocia contratos laborales (negociación colectiva) con los empleadores. El propósito más común de estas asociaciones o sindicatos es "mantener o mejorar las condiciones de su empleo". Esto puede incluir la negociación de los salarios, normas de trabajo, procedimientos de quejas, las normas que rigen la contratación, despido y promoción de los trabajadores, las prestaciones, la seguridad laboral y las políticas.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_COMMUNISM_HELP', '+1 de felicidad [ICON_HAPPINESS_1] en la ciudad y +10% de ciencia [ICON_RESEARCH] por fraguas y fábricas.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_UNITED_FRONT_HELP', '+25% de probabilidad de capturar espías. [NEWLINE] -25% d eprobabilidad que el espía enemigo te robe una tecnología. [NEWLINE] +5 a la producción [ICON_PRODUCTION] por edificios de espionaje.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_BRANCH_RATIONALISM', 'Conocimiento.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_BRANCH_RATIONALISM_CAP', 'CONOCIMIENTO.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_CIV5_POLICY_RATIONALISM_HEADING', '{TXT_KEY_POLICY_BRANCH_RATIONALISM}', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_SOCIALPOLICY_RATIONALISM_HEADING3_TITLE', '{TXT_KEY_POLICY_BRANCH_RATIONALISM}', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_RATIONALISM_TEXT', 'El conocimiento es una particularidad de alguien o algo, que puede incluir datos, información, descripciones, o las habilidades adquiridas por la experiencia o la educación. Se puede referir a la comprensión teórica o práctica de un sujeto. Puede ser una habilidad práctica o la experiencia, o la comprensión teórica de un sujeto.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_SOCIALPOLICY_PIETY_HEADING3_BODY', 'La Devoción aumenta la felicidad, la cultura y la fe de la civilización que la adopte. La devoción está disponible una vez que se alcanza la época clásica. Este es un lugar ideal para adquirir políticas para los jugadores que buscan crear o expandir las religiones.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_SOCIALPOLICY_RATIONALISM_ADV_QUEST', '¿De qué trata la rama de la política social El Conocimiento?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_SOCIALPOLICY_RATIONALISM_HEADING3_BODY', 'La rama del conocimiento mejora la capacidad de la civilización de usar y generar ciencia, está disponible al entrar en el Renacimiento.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_TECH_RATIONALISM_HEADING4_BODY', 'Las políticas sociales de la rama del conocimiento están llenas de políticas que pueden aumentar su investigación. Esta rama está disponible en la época del Renacimiento. Ver las reglas sobre Políticas Sociales para obtener más información.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_TECH_RATIONALISM_HEADING4_TITLE', 'La Rama del Conocimiento.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_BRANCH_RATIONALISM_HELP', 'Bueno para investigar cosas nuevas a construir. [NEWLINE] [NEWLINE] [COLOR_POSITIVE_TEXT] Al iniciarse [ENDCOLOR] [NEWLINE] +1 Gran científico [ICON_TEAM_7] en la Capital. [NEWLINE] +1 de Ciencia [ICON_RESEARCH] en los edificios de Ciencia. [NEWLINE ] Se puede comprar grandes científicos con fe [ICON_PEACE] una vez que entramos en la era industrial. [NEWLINE] [NEWLINE] [COLOR_POSITIVE_TEXT] Al concluirse [ENDCOLOR] [COLOR_GREY] (terminar todas las políticas en este árbol) [ENDCOLOR] [NEWLINE] +2 tecnologías [ICON_TEAM_5] gratis.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_SCIENTIFIC_REVOLUTION_HELP', '+15% de ciencia [ICON_RESEARCH] por escuelas públicas. [NEWLINE] Construye un edificio de ciencia en sus 4 ciudades más antiguas.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_SOVEREIGNTY_HELP', '+15% de ciencia [ICON_RESEARCH] cuando la nación esta feliz [ICON_HAPPINESS_1].', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_HUMANISM_HELP', '+1 de felicidad [ICON_HAPPINESS_1] en la ciudad por Universidades, Escuelas Públicas, y Laboratorios de Investigación.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_FREE_THOUGHT_HELP', '+1 de ciencia [ICON_RESEARCH] por Pueblos. [NEWLINE] +2 de [ICON_RESEARCH] ciencia por Puertos.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_SECULARISM_HELP', '+2 de ciencia [ICON_RESEARCH] por especialistas.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_BRANCH_AUTOCRACY', 'Nacionalismo.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_BRANCH_AUTOCRACY_CAP', 'NACIONALISMO.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_CIV5_POLICY_AUTOCRACY_HEADING', '{TXT_KEY_POLICY_BRANCH_AUTOCRACY}', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_SOCIALPOLICY_AUTOCRACY_HEADING3_TITLE', '{TXT_KEY_POLICY_BRANCH_AUTOCRACY}', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_AUTOCRACY_TEXT', 'El nacionalismo pone de relieve la importancia y la singularidad de la situación. El Estado es la madre y el padre del pueblo, y la gente ama el estado. Todo el mundo tiene la bandera nacional izando en su casa, y todo el mundo asiste al desfile en la fiesta nacional lleno de personal militar y armamento. Estados nacionalistas son a menudo fuertes, ya que las personas trabajan incansablemente para el progreso del estado. Sin embargo, la crítica interna del Estado puede sofocar, lo que puede limitar la capacidad del Estado para corregirse si se sale de curso.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_SOCIALPOLICY_AUTOCRACY_ADV_QUEST', '¿De qué trata la política social El Nacionalismo?', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_SOCIALPOLICY_AUTOCRACY_HEADING3_BODY', 'Esta rama es adecuada para aquellos que luchan por mantener su imperio feliz. Es ideal para los jugadores que buscan una Victoria por dominación. Esta política se desbloquea al entrar en el Renacimiento, y no puede esta activa al mismo tiempo que la libertad.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_BRANCH_AUTOCRACY_HELP', 'Bueno para la felicidad y la conquista. [NEWLINE] [NEWLINE] [COLOR_POSITIVE_TEXT] Al iniciarse [ENDCOLOR] [NEWLINE] +1 de felicidad [ICON_HAPPINESS_1] en la ciudad por Monumentos. [NEWLINE] Puede comprar Grandes Generales y Almirantes con fe [ICON_PEACE]. [NEWLINE] [NEWLINE] [COLOR_POSITIVE_TEXT] Al concluirse [ENDCOLOR] [COLOR_GREY] (terminar todas las políticas en este árbol) [ENDCOLOR] [NEWLINE] +30 de experiencia [ICON_WAR] para las unidades. [NEWLINE] [NEWLINE] [COLOR_WARNING_TEXT] el nacionalismo no puede estar activo al mismo tiempo que la Libertad. [ENDCOLOR]', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_POLICE_STATE_HELP', '+2 de felicidad [ICON_HAPPINESS_1] en la Ciudad por edificios de espionaje. [NEWLINE] +100% a la producción [ICON_PRODUCTION] para la construcción de edificios de espionaje.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_MILITARISM_HELP', '-25% por gastos [ICON_GOLD] y mantenimiento de unidades.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_NATIONALISM_HELP', '+0.5 de felicidad [ICON_HAPPINESS_1] en la ciudad por cada especialista. [NEWLINE] +3 de felicidad [ICON_HAPPINESS_1] en la ciudad por Delegaciones.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_FASCISM_HELP', 'Tu imperio saquea tesoros culturales [ICON_CULTURE] de las ciudades capturadas.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_CONSCRIPTION', 'Servicio militar obligatorio.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_CONSCRIPTION_HELP', '+10 a la tasa de sanación para las unidades.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_CONSCRIPTION_TEXT', 'La conscripción es el reclutamiento obligatorio de las personas en algún tipo de servicio nacional, con mayor frecuencia el servicio militar. En todo el reinado de Hammurabi (1791-1750 A.C), el imperio babilónico utilizó un sistema de conscripción llamado Ilkum. En la época medieval, en las condiciones feudales para el mantenimiento de la tierra, la mayoría de los campesinos y los hombres libres eran susceptibles de proporcionar un hombre mayor de edad por familia para el servicio militar cuando era necesario, ya sea para el rey o el señor del lugar. Los gravámenes planteados de esta manera lucharon como infantería bajo los superiores locales. Aunque las leyes exactas variaron enormemente según el país y el período, por lo general estos gravámenes sólo estaban obligados a luchar durante uno a tres meses. La mayoría eran agricultores de subsistencia, y estaba en el interés de todos enviar a casa a los hombres para la cosecha. Bajo este sistema los elegibles fueron obligados a servir en el ejército real en tiempo de guerra. En tiempos de paz se les exigía proporcionar mano de obra para otras actividades del Estado. A cambio de este servicio, las personas sujetas a ella se ganaron el derecho a poseer tierras. [NEWLINE] [NEWLINE] El moderno sistema casi universal de reclutamiento nacional para los hombres jóvenes se remonta a la Revolución Francesa en la década de 1790, donde se convirtió en la base de un ejército muy grande y poderoso. Mayoría de las naciones europeas más tarde copió el sistema en tiempo de paz, para que los hombres de cierta edad serviría de 1-3 años en servicio activo y luego fueran traslados a la fuerza de reserva.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_SOCIALPOLICY_FREEDOM_HEADING3_BODY', 'Libertad ofrece bonificaciones para las grandes ciudades. La libertad rige después de entrar en el Renacimiento, y no puede estar activa al mismo tiempo que el nacionalismo.', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_BRANCH_FREEDOM_HELP', 'Bueno para los imperios pacíficos con alta población. [NEWLINE] [NEWLINE] [COLOR_POSITIVE_TEXT] Al iniciarse [ENDCOLOR] [NEWLINE] +25% a la fuerza de combate [ICON_STRENGTH] por ciudades y unidades en territorio amigo. [NEWLINE] Puede comprar grandes artistas con fe [ICON_PEACE]. [NEWLINE] [NEWLINE] [COLOR_POSITIVE_TEXT] Al concluirse [ENDCOLOR] [COLOR_GREY] (terminar todas las políticas en este árbol) [ENDCOLOR] [NEWLINE] Los efectos por Maravillas Nacionales se duplican . [NEWLINE] +1 de población [ICON_CITIZEN] en sus ciudades. [NEWLINE] [NEWLINE] [COLOR_WARNING_TEXT] La libertad no puede estar activa al mismo tiempo que el nacionalismo. [ENDCOLOR]', '', '');
INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-14'), 'TXT_KEY_POLICY_FREE_TRADE', 'Libre Comercio.', '', '');
