INSERT INTO Civup_Language_ES_ES (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-03-13'), 'TXT_KEY_CIV5_IMPROVEMENTS_FORT_TEXT', '[COLOR_NEGATIVE_TEXT] Cuesta [ENDCOLOR] 3 [ICON_GOLD] de oro por turno de mantenimiento. [NEWLINE] [NEWLINE] Una fortaleza es una mejora especial que aumenta la bonificación defensiva de una casilla, y quita 20 de vida a los enemigos adyacentes. Sin embargo, estos efectos no se aplican en el territorio enemigo.', '', '');
