INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICIES_CULTURAL_VICTORY', 'Upon completing 6 Policy branches, you may construct the [COLOR_POSITIVE_TEXT]Utopia Project[ENDCOLOR] in one of your Cities, triggering a [COLOR_POSITIVE_TEXT]Cultural Victory[ENDCOLOR]!', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_BRANCH_LIBERTY_HELP', 'Good for quickly claiming early territory.[NEWLINE][NEWLINE][COLOR_POSITIVE_TEXT]Opener[ENDCOLOR][NEWLINE]+1 Palace in oldest 3 cities.[NEWLINE]+1 [ICON_CULTURE] Culture in all cities.[NEWLINE][NEWLINE][COLOR_POSITIVE_TEXT]Finisher[ENDCOLOR] [COLOR_GREY](finish all policies in this tree)[ENDCOLOR][NEWLINE]+3 [ICON_PRODUCTION] Production in cities with a Specialist.[NEWLINE]+1 [ICON_TEAM_2] Great Person of choice.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_COLLECTIVE_RULE_HELP', '+1 [ICON_TEAM_2] Settler at the Capital.[NEWLINE]+20% [ICON_PRODUCTION] Production for Settlers.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_MERITOCRACY_HELP', 'Gives a lump sum of [ICON_GOLD] Gold.[NEWLINE][NEWLINE]Reduces policy costs in large empires:[NEWLINE]cities producing approximately 15[ICON_CULTURE] Culture are revenue-neutral for policy rate.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_CITIZENSHIP_HELP', '+2 [ICON_TEAM_2] Workers at the Capital.[NEWLINE]+25% [ICON_WORKER] worker improvement speed.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_REPRESENTATION_HELP', '+1 [ICON_HAPPINESS_1] National Happiness in [ICON_CONNECTED] Connected cities.[NEWLINE]-5% [ICON_HAPPINESS_4] Unhappiness from [ICON_CITIZEN] Population in cities that are not [ICON_OCCUPIED] Occupied.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_REPUBLIC_HELP', '+2 [ICON_PRODUCTION] Production from [ICON_STRENGTH] Defense buildings.[NEWLINE]Constructs 1 [ICON_STRENGTH] Defense Building in current and future cities.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_BRANCH_TRADITION_HELP', 'Good for developing a few big cities.[NEWLINE][NEWLINE][COLOR_POSITIVE_TEXT]Opener[ENDCOLOR][NEWLINE]+1 [ICON_CULTURE] Culture from each surplus [ICON_HAPPINESS_1] National Happiness.[NEWLINE][NEWLINE][COLOR_POSITIVE_TEXT]Finisher[ENDCOLOR] [COLOR_GREY](finish all policies in this tree)[ENDCOLOR][NEWLINE]+50% [ICON_GREAT_PEOPLE] Great Person creation rate.[NEWLINE]+1 [ICON_HAPPINESS_1] City Happiness for Wonders.[NEWLINE]200% [ICON_CULTURE] cultural border expansion rate to tiles far from cities.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_ORAL_HISTORY', 'Storytellers', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_ORAL_HISTORY_HELP', '+2 [ICON_CULTURE] Culture for the Palace and Villages.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_ORAL_HISTORY_TEXT', 'Oral tradition is cultural material and tradition transmitted orally from one generation to another. The messages or testimony are verbally transmitted in speech or song and may take the form, for example, of folktales, sayings, ballads, songs, or chants. In this way, it is possible for a society to transmit verbal history, verbal literature, verbal law and other knowledge across generations without a writing system.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_CEREMONIAL_RITES', 'Ceremonial Rites', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_CEREMONIAL_RITES_HELP', '+2 [ICON_CULTURE] Culture from Cultural Buildings.[NEWLINE]Constructs 1 [ICON_CULTURE] Culture Building in your oldest 5 cities.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_CEREMONIAL_RITES_TEXT', 'A ceremony is an event of ritual significance performed on a special occasion. Ceremonies may have a physical display or theatrical component: dance, a procession, the laying on of hands. A declaratory verbal pronouncement may be associated with the occasion.[NEWLINE][NEWLINE]A ceremony may mark a rite of passage in a human life, marking the significance of birth, adulthood, initiation into a group, or death. Sometimes a ceremony may only be performed by a person with certain authority. Society-wide ceremonies may mark annual or seasonal or recurrent events, such as the vernal equinox, winter solstice and other annual astronomical positions.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_LANDED_ELITE_HELP', '+20% [ICON_FOOD] Surplus Food in each city.[NEWLINE]+2 [ICON_FOOD] Food from [ICON_STRENGTH] Defense buildings.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_OLIGARCHY_HELP', '+50% [ICON_RANGE_STRENGTH] Strength for garrisoned cities.[NEWLINE]Garrisoned units cost 0 [ICON_GOLD] Maintenance and gain 0.2 [ICON_WAR] Experience per turn.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_ARISTOCRACY_HELP', '+20% [ICON_PRODUCTION] Production for building Wonders.[NEWLINE]+2 [ICON_CULTURE] Culture from Wonders.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_MONARCHY_HELP', '+5 [ICON_RESEARCH] Science per turn.[NEWLINE]+5% [ICON_RESEARCH] Science from Science Buildings.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_BRANCH_HONOR_HELP', 'Good for fighting barbarians, and conquering enemies.[NEWLINE][NEWLINE][COLOR_POSITIVE_TEXT]Opener[ENDCOLOR][NEWLINE]Reveals Barbarian camps.[NEWLINE]+10% Strength against Barbarians.[NEWLINE]Killing barbarians gives 5 [ICON_CULTURE] Culture per 1[ICON_STRENGTH] Strength.[NEWLINE][NEWLINE][COLOR_POSITIVE_TEXT]Finisher[ENDCOLOR] [COLOR_GREY](finish all policies in this tree)[ENDCOLOR][NEWLINE]+1 [ICON_HAPPINESS_1] City Happiness and +4 [ICON_CULTURE] Culture for cities with a Garrison or Barracks.', '', '');
