INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_BRANCH_COMMERCE_CAP', 'WEALTH', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_CIV5_POLICY_COMMERCE_HEADING', '{TXT_KEY_POLICY_BRANCH_COMMERCE}', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_SOCIALPOLICY_COMMERCE_HEADING3_TITLE', '{TXT_KEY_POLICY_BRANCH_COMMERCE}', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_COMMERCE_TEXT', 'Wealth is the abundance of valuable resources or material possessions. The concept of wealth is significant in all areas of economics, yet the meaning of wealth is context-dependent and there is no universally agreed upon definition. At the most general level wealth is &quot;anything of value&quot;, which captures both the subjective nature of the word, and the idea that it is not a fixed or static concept.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_SOCIALPOLICY_COMMERCE_ADV_QUEST', 'What is the Wealth social policy branch about?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_SOCIALPOLICY_COMMERCE_HEADING3_BODY', 'The branch of Wealth focuses on developing wide territory, with a national supply of [ICON_GOLD] Gold created by core cities to purchase new units and buildings in undeveloped frontier cities.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_TECH_COMMERCE_HEADING4_BODY', 'The branch of Wealth focuses on developing wide territory, with a national supply of [ICON_GOLD] Gold created by core cities to purchase new units and buildings in undeveloped frontier cities. See the rules on Social Policies for details.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_TECH_COMMERCE_HEADING4_TITLE', 'The Wealth Branch', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_BRANCH_COMMERCE_HELP', 'Good for building things faster in cities.[NEWLINE][NEWLINE][COLOR_POSITIVE_TEXT]Opener[ENDCOLOR][NEWLINE]+15% [ICON_GOLD] gross national income.[NEWLINE]Can purchase Great Merchants with [ICON_PEACE] Faith once you enter the Industrial era.[NEWLINE][NEWLINE][COLOR_POSITIVE_TEXT]Finisher[ENDCOLOR] [COLOR_GREY](finish all policies in this tree)[ENDCOLOR][NEWLINE]+1 [ICON_HAPPINESS_1] National Happiness from each Luxury resource, including surplus copies.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_MERCANTILISM_HELP', '+2[ICON_MOVES] Movement for non-combat units.[NEWLINE]+25% [ICON_GOLD] Income from [ICON_CONNECTED] Domestic Trade.[NEWLINE]-25% [ICON_GOLD] Cost for roads and railroads.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_GUILDS', 'Guilds', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_GUILDS_HELP', '+1 [ICON_TEAM_7] Great Merchant at the Capital.[NEWLINE]+100% [ICON_GOLD] Gold from Great Merchant trade missions.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_GUILDS_TEXT', 'A guild is an association of craftsmen in a particular trade. The earliest types of guild were formed as confraternities of workers. They were organized in a manner something between a trade union, a cartel, and a secret society. They often depended on grants of letters patent by a monarch or other authority to enforce the flow of trade to their self-employed members, and to retain ownership of tools and the supply of materials.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_PROTECTIONISM_HELP', '-20% [ICON_GOLD] Purchase cost for units and buildings.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_PATENT_LAW', 'Patent Law', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_PATENT_LAW_HELP', '+1 [ICON_GOLD] Gold from Villages.[NEWLINE]+2 [ICON_GOLD] Gold from Harbors.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_PATENT_LAW_TEXT', 'A patent is a set of exclusive rights granted by a state (national government) to an inventor or their assignee for a limited period of time in exchange for the public disclosure of an invention.[NEWLINE][NEWLINE]Patents in the modern sense originated in 1474, when the Republic of Venice enacted a decree that new and inventive devices, once put into practice, had to be communicated to the Republic to obtain the right to prevent others from using them.[NEWLINE][NEWLINE]England followed with the Statute of Monopolies in 1623 under King James I, which declared that patents could only be granted for "projects of new invention."  During the reign of Queen Anne (1702â€“14), the lawyers of the English Court developed the requirement that a written description of the invention must be submitted.  The patent system in many other countries is based on British law and can be traced back to the Statute of Monopolies.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_MERCHANT_NAVY_HELP', '+1[ICON_MOVES] Moves and +1 Sight for military ships.[NEWLINE]+2 [ICON_RESEARCH] Science for Coastal Cities.[NEWLINE]+2 [ICON_RESEARCH] Science for Naval Buildings.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_BRANCH_PATRONAGE_HELP', 'Good for interacting with City-States.[NEWLINE][NEWLINE][COLOR_POSITIVE_TEXT]Opener[ENDCOLOR][NEWLINE]City-States resting influence is 20 [ICON_INFLUENCE].[NEWLINE][NEWLINE][COLOR_POSITIVE_TEXT]Finisher[ENDCOLOR] [COLOR_GREY](finish all policies in this tree)[ENDCOLOR][NEWLINE]+2 [ICON_HAPPINESS_1] National Happiness for each City-State friend, and +3 for each ally.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_PHILANTHROPY_HELP', '+33% [ICON_INFLUENCE] Influence from [ICON_GOLD] Gold gifts to City-States.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_CULTURAL_DIPLOMACY_HELP', '[ICON_INFLUENCE] Influence with City-States degrades 33% slower than normal.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_AESTHETICS_HELP', '+20 instant [ICON_INFLUENCE] Influence with City-States.[NEWLINE]Other players'' [ICON_INFLUENCE] Influence with City-States decreases 25% faster.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_SCHOLASTICISM_HELP', 'City-State allies provide [ICON_RESEARCH] Science.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_TRADE_PACT', 'Trade Pact', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_TRADE_PACT_HELP', 'City-state friends and allies increase [ICON_PRODUCTION] Production in your cities.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_TRADE_PACT_TEXT', 'Free trade is a system of trade policy that allows traders to trade across national boundaries without interference from the respective governments. Due to the economic principle of comparative advantage, this provides trading partners with mutual gains. Under this policy prices are the sole determinant of resource allocation, and a close reflection of supply and demand.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_BRANCH_ORDER_HELP', 'Good for expanding and developing modern cities.[NEWLINE][NEWLINE][COLOR_POSITIVE_TEXT]Opener[ENDCOLOR][NEWLINE]+10% [ICON_PRODUCTION] Production in all cities.[NEWLINE]+1 [ICON_TEAM_7] Great Engineer at the Capital.[NEWLINE]Can purchase Great Engineers with [ICON_PEACE] Faith.[NEWLINE][NEWLINE][COLOR_POSITIVE_TEXT]Finisher[ENDCOLOR] [COLOR_GREY](finish all policies in this tree)[ENDCOLOR][NEWLINE]Gives 10 turns of [ICON_PRODUCTION] Production in all cities.[NEWLINE]+10% [ICON_PRODUCTION] Production from factories and power plants.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_PLANNED_ECONOMY_HELP', 'Construct Production buildings at double speed.', '', '');
