INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_UNITED_FRONT_HELP', '+25% chance to catch spies.[NEWLINE]-25% enemy spy tech steal rate.[NEWLINE]+5 [ICON_PRODUCTION] Production from Espionage buildings.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_BRANCH_RATIONALISM', 'Knowledge', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_BRANCH_RATIONALISM_CAP', 'KNOWLEDGE', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_CIV5_POLICY_RATIONALISM_HEADING', '{TXT_KEY_POLICY_BRANCH_RATIONALISM}', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_SOCIALPOLICY_RATIONALISM_HEADING3_TITLE', '{TXT_KEY_POLICY_BRANCH_RATIONALISM}', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_RATIONALISM_TEXT', 'Knowledge is a familiarity with someone or something, which can include facts, information, descriptions, or skills acquired through experience or education. It can refer to the theoretical or practical understanding of a subject. It can be practical skill or expertise, or the theoretical understanding of a subject.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_SOCIALPOLICY_PIETY_HEADING3_BODY', 'Piety increases the Happiness, Culture, and Faith of the adopting civ. Piety becomes available once the Classical Era is reached. This is an ideal place to acquire policies for players seeking to create or expand religions.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_SOCIALPOLICY_RATIONALISM_ADV_QUEST', 'What is the Knowledge social policy branch about?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_SOCIALPOLICY_RATIONALISM_HEADING3_BODY', 'The branch of Knowledge improves the civilization''s ability to use and generate Science, becoming available upon entering the Renaissance Era.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_TECH_RATIONALISM_HEADING4_BODY', 'The Knowledge branch of social policies is full of policies which can increase your research. This branch becomes available in the Renaissance era. See the rules on Social Policies for details.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_TECH_RATIONALISM_HEADING4_TITLE', 'The Knowledge Branch', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_BRANCH_RATIONALISM_HELP', 'Good for researching new things to build.[NEWLINE][NEWLINE][COLOR_POSITIVE_TEXT]Opener[ENDCOLOR][NEWLINE]+1 [ICON_TEAM_7] Great Scientist at the Capital.[NEWLINE]+1 [ICON_RESEARCH] Science on Science buildings.[NEWLINE]Can purchase Great Scientists with [ICON_PEACE] Faith once you enter the Industrial era.[NEWLINE][NEWLINE][COLOR_POSITIVE_TEXT]Finisher[ENDCOLOR] [COLOR_GREY](finish all policies in this tree)[ENDCOLOR][NEWLINE]+2 instant [ICON_TEAM_5] Technologies.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_SCIENTIFIC_REVOLUTION_HELP', '+15% [ICON_RESEARCH] Science from Public Schools.[NEWLINE]Constructs a Science building in your oldest 4 cities.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_SOVEREIGNTY_HELP', '+20% [ICON_RESEARCH] Science when the nation is [ICON_HAPPINESS_1] Happy.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_HUMANISM_HELP', '+1 [ICON_HAPPINESS_1] City Happiness from Universities, Public Schools, and Research Labs.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_FREE_THOUGHT_HELP', '+1 [ICON_RESEARCH] Science from Villages.[NEWLINE]+2 [ICON_RESEARCH] Science from Harbors.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_SECULARISM_HELP', '+2 [ICON_RESEARCH] Science from Specialists.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_BRANCH_AUTOCRACY', 'Nationalism', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_BRANCH_AUTOCRACY_CAP', 'NATIONALISM', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_CIV5_POLICY_AUTOCRACY_HEADING', '{TXT_KEY_POLICY_BRANCH_AUTOCRACY}', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_SOCIALPOLICY_AUTOCRACY_HEADING3_TITLE', '{TXT_KEY_POLICY_BRANCH_AUTOCRACY}', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_AUTOCRACY_TEXT', 'Nationalism emphasizes the importance and uniqueness of the state. The state is the mother and father of the people, and the people love the state. Everybody has the national flag flying from their home, and everybody attends the founding day parade of military personnel and hardware. Nationalist states are often strong, as the people work tirelessly for the state''s advancement. However, internal criticism of the state may be stifled, which can limit the state''s ability to correct itself if goes off course.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_SOCIALPOLICY_AUTOCRACY_ADV_QUEST', 'What is the Nationalism social policy about?', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_SOCIALPOLICY_AUTOCRACY_HEADING3_BODY', 'This branch is well suited for those struggling to keep their empire happy. It is ideal for players who seek a Domination Victory. This policy unlocks upon entering the Renaissance Era, and cannot be active at the same time as Freedom.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_BRANCH_AUTOCRACY_HELP', 'Good for happiness and conquest.[NEWLINE][NEWLINE][COLOR_POSITIVE_TEXT]Opener[ENDCOLOR][NEWLINE]+1 [ICON_HAPPINESS_1] City Happiness from Monuments.[NEWLINE]Can purchase Great Generals and Admirals with [ICON_PEACE] Faith.[NEWLINE][NEWLINE][COLOR_POSITIVE_TEXT]Finisher[ENDCOLOR] [COLOR_GREY](finish all policies in this tree)[ENDCOLOR][NEWLINE]+30 [ICON_WAR] Experience for units.[NEWLINE][NEWLINE][COLOR_WARNING_TEXT]Nationalism cannot be active with Freedom.[ENDCOLOR]', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_POLICE_STATE_HELP', '+2 [ICON_HAPPINESS_1] City Happiness from Espionage buildings.[NEWLINE]+100% [ICON_PRODUCTION] Production for building Espionage buildings.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_MILITARISM_HELP', '-25% [ICON_GOLD] Purchase and Maintenance costs for units.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_NATIONALISM_HELP', '+0.5 [ICON_HAPPINESS_1] City Happiness from each Specialist.[NEWLINE]+3 [ICON_HAPPINESS_1] City Happiness from Courthouses.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_FASCISM_HELP', 'Your empire plunders [ICON_CULTURE] Cultural treasures from captured cities.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_CONSCRIPTION', 'Conscription', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_CONSCRIPTION_HELP', '+10 heal rate for units.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_CONSCRIPTION_TEXT', '(todo)', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_SOCIALPOLICY_FREEDOM_HEADING3_BODY', 'Freedom provides bonuses for large cities. Freedom becomes available upon entering the Renaissance Era, and cannot be active at the same time as Nationalism.', '', '');
INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_BRANCH_FREEDOM_HELP', 'Good for peaceful empires with high population.[NEWLINE][NEWLINE][COLOR_POSITIVE_TEXT]Opener[ENDCOLOR][NEWLINE]+25% [ICON_STRENGTH] Combat Strength for cities and units in friendly territory.[NEWLINE]Can purchase Great Artists with [ICON_PEACE] Faith.[NEWLINE][NEWLINE][COLOR_POSITIVE_TEXT]Finisher[ENDCOLOR] [COLOR_GREY](finish all policies in this tree)[ENDCOLOR][NEWLINE]Double effects from National Wonders.[NEWLINE]+1 [ICON_CITIZEN] Population in your cities.[NEWLINE][NEWLINE][COLOR_WARNING_TEXT]Freedom cannot be active with Nationalism.[ENDCOLOR]', '', '');
