INSERT INTO Civup_Language_EN_US (DateModified, Tag, Text, Gender, Plurality) VALUES (date('2013-01-20'), 'TXT_KEY_POLICY_FREE_TRADE_HELP', '+100%[ICON_GOLD] Gold from Mutual Open Borders.[NEWLINE]+50% [ICON_RESEARCH] Science from Research Agreements.', '', '');
